# Vibe coding iteration cycle:

# Version 1: Basic (AI helps you start)
def greet(name):
    return f"Hello {name}"

# Version 2: Better (You ask AI to improve)
def greet(name, style="casual"):
    """Greet someone with different styles."""
    styles = {
        "casual": f"Hey {name}!",
        "formal": f"Good day, {name}.",
        "excited": f"OMG HI {name}!!!"
    }
    return styles.get(style, f"Hello {name}")

# Version 3: Production (AI adds error handling)
def greet(name, style="casual"):
    """Greet someone with different styles.

    Args:
        name: Person's name
        style: casual, formal, or excited

    Returns:
        Greeting string
    """
    if not name or not isinstance(name, str):
        return "Hello there!"

    styles = {
        "casual": f"Hey {name.strip()}!",
        "formal": f"Good day, {name.strip()}.",
        "excited": f"OMG HI {name.strip().upper()}!!!"
    }
    return styles.get(style, f"Hello {name.strip()}")

print(greet("Alice", "excited"))

# Each iteration adds value.
# You go from idea ‚Üí production code FAST!

