def my_coding_journey():
    """Reflect on your learning journey.

    Returns:
        Dictionary with your coding journey
    """
    return {
        "where_i_started": "Describe your starting point...",
        "what_i_learned": [
            # List key concepts you learned
        ],
        "what_i_will_build": [
            # List 3 projects you want to create
        ],
        "commitment": "Your commitment to yourself..."
    }

# Call it and see your journey:
journey = my_coding_journey()
print("MY CODING JOURNEY")
print("="*50)
for key, value in journey.items():
    print(f"\n{key.replace('_', ' ').title()}:")
    if isinstance(value, list):
        for item in value:
            print(f"  - {item}")
    else:
        print(f"  {value}")
