person = {'name': 'Bob', 'age': 30}

# Add a new key-value pair
person['city'] = 'Boston'
print(person)  # {'name': 'Bob', 'age': 30, 'city': 'Boston'}

# Modify existing value
person['age'] = 31
print(person)  # {'name': 'Bob', 'age': 31, 'city': 'Boston'}

person = {'name': 'Alice', 'age': 25, 'city': 'NYC'}

# Remove a key-value pair
del person['city']
print(person)  # {'name': 'Alice', 'age': 25}

# Remove and get the value
age = person.pop('age')
print(age)     # 25
print(person)  # {'name': 'Alice'}

person = {'name': 'Bob', 'age': 30}

if 'name' in person:
    print(f"Name is {person['name']}")

if 'email' not in person:
    print("No email found")

# Check multiple keys
required = ['name', 'age', 'city']
missing = [key for key in required if key not in person]
print(f"Missing: {missing}")  # Missing: ['city']

person = {'name': 'Bob', 'age': 30, 'city': 'Boston'}

# Loop through keys
for key in person:
    print(f"{key}: {person[key]}")

print("---")

# Loop through keys and values together (better!)
for key, value in person.items():
    print(f"{key}: {value}")

print("---")

# Loop through just values
for value in person.values():
    print(value)

# Create a dictionary of squares
squares = {x: x**2 for x in range(1, 6)}
print(squares)  # {1: 1, 2: 4, 3: 9, 4: 16, 5: 25}

# Convert lists to dictionary
keys = ['name', 'age', 'city']
values = ['Alice', 25, 'NYC']
person = {k: v for k, v in zip(keys, values)}
print(person)  # {'name': 'Alice', 'age': 25, 'city': 'NYC'}
