def double(x):
    return x * 2

def quadruple(x):
    return double(double(x))

result = quadruple(5)
print(result)  # 20 (5 * 2 * 2)

def add(a, b):
    return a + b

def subtract(a, b):
    return a - b

def multiply(a, b):
    return a * b

def divide(a, b):
    if b == 0:
        return "Cannot divide by zero!"
    return a / b

# Use the calculator
print(add(10, 5))       # 15
print(subtract(10, 5))  # 5
print(multiply(10, 5))  # 50
print(divide(10, 5))    # 2.0
print(divide(10, 0))    # Cannot divide by zero!
